	        const angle = this.angleStep * this.direction * (1.1 - alpha);
	        this.controls.spin(this.axis, angle * stats.lastDuration / 16);
	        this.angleSum += this.angleStep;
	        if (this.angleSum >= this.angleEnd) {
	            this.direction *= -1;
	            this.angleSum = -this.angleEnd;
	        }
