	    _init(valueFrom, valueTo, callback) {
	        this.valueFrom = valueFrom;
	        this.valueTo = valueTo;
	        this.callback = callback;
	    }
	    _tick( /* stats */) {
	        this.callback(lerp(this.valueFrom, this.valueTo, this.alpha));
	    }
