	class MeshPicker extends ShapePicker {
	    constructor(shape, mesh) {
	        super(shape);
	        this.mesh = mesh;
	    }
	    get type() { return 'mesh'; }
	    getObject( /* pid */) {
	        const m = this.mesh;
	        return {
	            shape: this.shape,
	            name: m.name,
	            serial: m.serial
	        };
	    }
	    _getPosition( /* pid */) {
	        if (!this.__position) {
	            this.__position = calculateMeanVector3(this.mesh.position);
	        }
	        return this.__position;
	    }
	}
