	    let at = atomName.toUpperCase();
	    let begin = 0, end = 0;
	    for (let i = 0; i < at.length; i++) {
	        if (at.charCodeAt(i) < 65) {
	            if (end > 0)
	                break;
	            ++begin;
	        }
	        else
	            end = i + 1;
	    }
	    if (begin > 0 || end < at.length)
	        at = at.substring(begin, end);
	    const n = at.length;
	    if (n === 0)
	        return '';
	    if (n === 1)
	        return at;
	    if (n === 2) {
	        if (elm2.indexOf(at) !== -1)
	            return at;
	        if (elm1.indexOf(at[0]) !== -1)
	            return at[0];
	        if (at in AtomicNumbers)
	            return at;
	    }
	    if (n >= 3) {
	        if (elm1.indexOf(at[0]) !== -1)
	            return at[0];
	    }
