	function decodeMsgpack(buffer) {
	  // Loosely based on
	  // The MIT License (MIT)
	  // Copyright (c) 2013 Tim Caswell <tim@creationix.com>
	  // https://github.com/creationix/msgpack-js
	  var offset = 0;
	  var dataView = new DataView(buffer.buffer);

	  /**
	   * decode all key-value pairs of a map into an object
	   * @param  {Integer} length - number of key-value pairs
	   * @return {Object} decoded map
	   */
	  function map(length) {
	    var value = {};
	    for (var i = 0; i < length; i++) {
	      var key = parse();
	      value[key] = parse();
	    }
	    return value;
	  }

	  /**
	   * decode binary array
	   * @param  {Integer} length - number of elements in the array
	   * @return {Uint8Array} decoded array
	   */
	  function bin(length) {
	    var value = buffer.subarray(offset, offset + length);
	    offset += length;
	    return value;
	  }

	  /**
	   * decode string
	   * @param  {Integer} length - number string characters
	   * @return {String} decoded string
	   */
	  function str(length) {
	    var array = buffer.subarray(offset, offset + length);
	    offset += length;
	    // limit number of arguments to String.fromCharCode to something
	    // browsers can handle, see http://stackoverflow.com/a/22747272
	    var chunkSize = 0xffff;
	    if(length > chunkSize){
	      var c = [];
	      for(var i = 0; i < array.length; i += chunkSize) {
	        c.push(String.fromCharCode.apply(
	          null, array.subarray(i, i + chunkSize)
	        ));
	      }
	      return c.join("");
	    }else {
	      return String.fromCharCode.apply(null, array);
	    }
	  }

	  /**
	   * decode array
	   * @param  {Integer} length - number of array elements
	   * @return {Array} decoded array
	   */
	  function array(length) {
	    var value = new Array(length);
	    for (var i = 0; i < length; i++) {
	      value[i] = parse();
	    }
	    return value;
	  }

	  /**
	   * recursively parse the MessagePack data
	   * @return {Object|Array|String|Number|Boolean|null} decoded MessagePack data
	   */
	  function parse() {
	    var type = buffer[offset];
	    var value, length;
	    // Positive FixInt
	    if ((type & 0x80) === 0x00) {
	      offset++;
	      return type;
	    }
	    // FixMap
	    if ((type & 0xf0) === 0x80) {
	      length = type & 0x0f;
	      offset++;
	      return map(length);
	    }
	    // FixArray
	    if ((type & 0xf0) === 0x90) {
	      length = type & 0x0f;
	      offset++;
	      return array(length);
	    }
	    // FixStr
	    if ((type & 0xe0) === 0xa0) {
	      length = type & 0x1f;
	      offset++;
	      return str(length);
	    }
	    // Negative FixInt
	    if ((type & 0xe0) === 0xe0) {
	      value = dataView.getInt8(offset);
	      offset++;
	      return value;
	    }
	    switch (type) {
	    // nil
	    case 0xc0:
	      offset++;
	      return null;
	    // 0xc1: (never used, could be employed for padding)
	    // false
	    case 0xc2:
	      offset++;
	      return false;
	    // true
	    case 0xc3:
	      offset++;
	      return true;
	    // bin 8
	    case 0xc4:
	      length = dataView.getUint8(offset + 1);
	      offset += 2;
	      return bin(length);
	    // bin 16
	    case 0xc5:
	      length = dataView.getUint16(offset + 1);
	      offset += 3;
	      return bin(length);
	    // bin 32
	    case 0xc6:
	      length = dataView.getUint32(offset + 1);
	      offset += 5;
	      return bin(length);
	    // // ext 8
	    // case 0xc7:
	    //   length = dataView.getUint8(offset + 1);
	    //   extType = dataView.getUint8(offset + 2);
	    //   offset += 3;
	    //   return [extType, bin(length)];
	    // // ext 16
	    // case 0xc8:
	    //   length = dataView.getUint16(offset + 1);
	    //   extType = dataView.getUint8(offset + 3);
	    //   offset += 4;
	    //   return [extType, bin(length)];
	    // // ext 32
	    // case 0xc9:
	    //   length = dataView.getUint32(offset + 1);
	    //   extType = dataView.getUint8(offset + 5);
	    //   offset += 6;
	    //   return [extType, bin(length)];
	    // float 32
	    case 0xca:
	      value = dataView.getFloat32(offset + 1);
	      offset += 5;
	      return value;
	    // float 64
	    case 0xcb:
	      value = dataView.getFloat64(offset + 1);
	      offset += 9;
	      return value;
	    // uint8
	    case 0xcc:
	      value = buffer[offset + 1];
	      offset += 2;
	      return value;
	    // uint 16
	    case 0xcd:
	      value = dataView.getUint16(offset + 1);
	      offset += 3;
	      return value;
	    // uint 32
	    case 0xce:
	      value = dataView.getUint32(offset + 1);
	      offset += 5;
	      return value;
	    // // uint64
	    // case 0xcf:
	    //   // FIXME not available/representable in JS
	    //   // largest possible int in JS is 2^53
	    //   // value = dataView.getUint64(offset + 1);
	    //   offset += 9;
	    //   return 0;
	    // int 8
	    case 0xd0:
	      value = dataView.getInt8(offset + 1);
	      offset += 2;
	      return value;
	    // int 16
	    case 0xd1:
	      value = dataView.getInt16(offset + 1);
	      offset += 3;
	      return value;
	    // int 32
	    case 0xd2:
	      value = dataView.getInt32(offset + 1);
	      offset += 5;
	      return value;
	    // // int 64
	    // case 0xd3:
	    //   // FIXME not available/representable in JS
	    //   // largest possible int in JS is 2^53
	    //   // value = dataView.getInt64(offset + 1);
	    //   offset += 9;
	    //   return 0;

	    // // fixext 1
	    // case 0xd4:
	    //   extType = dataView.getUint8(offset + 1);
	    //   offset += 2;
	    //   return [extType, bin(1)];
	    // // fixext 2
	    // case 0xd5:
	    //   extType = dataView.getUint8(offset + 1);
	    //   offset += 2;
	    //   return [extType, bin(2)];
	    // // fixext 4
	    // case 0xd6:
	    //   extType = dataView.getUint8(offset + 1);
	    //   offset += 2;
	    //   return [extType, bin(4)];
	    // // fixext 8
	    // case 0xd7:
	    //   extType = dataView.getUint8(offset + 1);
	    //   offset += 2;
	    //   return [extType, bin(8)];
	    // // fixext 16
	    // case 0xd8:
	    //   extType = dataView.getUint8(offset + 1);
	    //   offset += 2;
	    //   return [extType, bin(16)];
	    // str 8
	    case 0xd9:
	      length = dataView.getUint8(offset + 1);
	      offset += 2;
	      return str(length);
	    // str 16
	    case 0xda:
	      length = dataView.getUint16(offset + 1);
	      offset += 3;
	      return str(length);
	    // str 32
	    case 0xdb:
	      length = dataView.getUint32(offset + 1);
	      offset += 5;
	      return str(length);
	    // array 16
	    case 0xdc:
	      length = dataView.getUint16(offset + 1);
	      offset += 3;
	      return array(length);
	    // array 32
	    case 0xdd:
	      length = dataView.getUint32(offset + 1);
	      offset += 5;
	      return array(length);
	    // map 16:
	    case 0xde:
	      length = dataView.getUint16(offset + 1);
	      offset += 3;
	      return map(length);
	    // map 32
	    case 0xdf:
	      length = dataView.getUint32(offset + 1);
	      offset += 5;
	      return map(length);
	    }

	    throw new Error("Unknown type 0x" + type.toString(16));
	  }

	  // start the recursive parsing
	  return parse();
	}

