    const p = Object.assign(getDataInfo(file), params);
    let loader;
    if (ParserRegistry.names.includes(p.ext)) {
        loader = new ParserLoader(p.src, p);
    }
    else if (ScriptExtensions.includes(p.ext)) {
        loader = new ScriptLoader(p.src, p);
    }
