        this.colorReverse = defaults(p.colorReverse, false);
        this.colorValue = defaults(p.colorValue, 0x909090);
        this.colorDomain = defaults(p.colorDomain, undefined);
        this.colorMode = defaults(p.colorMode, 'hcl');
