    if (tokens.includes('doubleClick'))
        type = 'doubleClick';
    if (tokens.includes('hover'))
        type = 'hover';
    if (tokens.includes('clickPick'))
        type = 'clickPick';
    if (tokens.includes('hoverPick'))
        type = 'hoverPick';
